function median(arr){ if(!arr.length) return 0; const s=[...arr].sort((a,b)=>a-b); const m=Math.floor(s.length/2); return s.length%2? s[m] : Math.round((s[m-1]+s[m])/2); }

try {
  const all = $input.all().map(i=>i.json).filter(v=>!v.error);
  if (!all.length) return [{ json: { error: 'æ²¡æœ‰æ‰¾åˆ°ç¬¦åˆæ¡ä»¶çš„è§†é¢‘' } }];

  const byViews = [...all].sort((a,b)=>b.viewCount - a.viewCount);

  // æ ‡ç­¾é¢‘æ¬¡ï¼ˆæ™®é€š & æŒ‰è§‚çœ‹é‡åŠ æƒï¼‰
  const freq = {}, weighted = {};
  for (const v of all) {
    (v.tags || []).forEach(t => {
      freq[t] = (freq[t] || 0) + 1;
      weighted[t] = (weighted[t] || 0) + v.viewCount;
    });
  }
  const topTags = Object.entries(freq).sort(([,a],[,b])=>b-a).slice(0,10).map(([tag,count])=>({tag,count}));
  const topTagsWeighted = Object.entries(weighted).sort(([,a],[,b])=>b-a).slice(0,10).map(([tag,views])=>({tag,views}));

  // é¢‘é“èšåˆ
  const channels = {};
  for (const v of all) {
    channels[v.channelId] ??= { channelTitle: v.channelTitle, channelUrl: v.channelUrl, videoCount:0, totalViews:0, totalLikes:0, totalComments:0 };
    const c = channels[v.channelId];
    c.videoCount++; c.totalViews+=v.viewCount; c.totalLikes+=v.likeCount; c.totalComments+=v.commentCount;
  }
  const topChannels = Object.values(channels).sort((a,b)=>b.totalViews - a.totalViews).slice(0,5);

  // ç»Ÿè®¡
  const totalVideos = all.length;
  const viewsArr = all.map(v=>v.viewCount);
  const totalViews = viewsArr.reduce((s,x)=>s+x,0);
  const averageViews = Math.round(totalViews / totalVideos);
  const medianViews = median(viewsArr);
  const totalLikes = all.reduce((s,v)=>s+v.likeCount,0);
  const totalComments = all.reduce((s,v)=>s+v.commentCount,0);
  const avgDuration = Math.round(all.reduce((s,v)=>s+v.duration,0) / totalVideos);

  const topByEngagement = [...all]
    .map(v => ({...v, engagementRate: v.viewCount ? (v.likeCount + v.commentCount) / v.viewCount : 0}))
    .sort((a,b)=>b.engagementRate - a.engagementRate)
    .slice(0,10);

  return [{
    json: {
      summary: {
        totalVideos, totalViews, averageViews, medianViews, totalLikes, totalComments, avgDuration,
        analysisDate: new Date().toISOString()
      },
      topVideos: byViews.slice(0,10),
      topByEngagement,
      topTags,
      topTagsWeighted,
      topChannels,
      allVideos: byViews
    }
  }];
} catch (e) {
  return [{ json: { error: e.message } }];
}

