function parseISODuration(s){
  if(!s) return 0;
  const m = s.match(/PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/);
  if(!m) return 0;
  const h = parseInt(m[1]||'0',10), mm = parseInt(m[2]||'0',10), ss = parseInt(m[3]||'0',10);
  return h*3600 + mm*60 + ss;
}

function extractItems(it){
  const j = it.json ?? it;
  if (Array.isArray(j)) return j;
  if (Array.isArray(j.items)) return j.items;
  if (j.body && Array.isArray(j.body.items)) return j.body.items;   // Full Response = true
  if (j.data && Array.isArray(j.data.items)) return j.data.items;   // æŸäº›æ¥å£å°è£…
  return [];
}

try {
  const carrier = $input.first()?.json ?? {};
  const flowCfg = $items('è®¾ç½®å‚æ•°', 0, 0)?.json ?? {};

  const minDuration = Number(carrier.minDuration ?? flowCfg.minDuration ?? 30);
  const minViews    = Number(carrier.minViews    ?? flowCfg.minViews    ?? 400);
  const afterDate   = (carrier.publishedAfter ?? flowCfg.publishedAfter)
    ? new Date(carrier.publishedAfter ?? flowCfg.publishedAfter)
    : null;

  const inputs = $input.all();
  const rawItems = inputs.flatMap(extractItems);

  const seen = new Set();
  const out = [];
  let totalIn = 0;

  for (const v of rawItems) {
    totalIn++;

    const id = v.id;
    if (!id || seen.has(id)) continue;

    const sn = v.snippet || {};
    const st = v.statistics || {};
    const cd = v.contentDetails || {};

    if (sn.liveBroadcastContent === 'live' || sn.liveBroadcastContent === 'upcoming') continue;

    const duration = parseISODuration(cd.duration);

    const viewCount    = parseInt(st?.viewCount    || '0', 10);
    const likeCount    = parseInt(st?.likeCount    || '0', 10);
    const commentCount = parseInt(st?.commentCount || '0', 10);

    if (!Number.isFinite(viewCount)) continue;

    const pubAt = sn.publishedAt ? new Date(sn.publishedAt) : null;

    if (minDuration > 0 && duration < minDuration) continue;
    if (afterDate && pubAt && pubAt < afterDate)   continue;
    if (pubAt && pubAt > new Date())               continue;
    if (viewCount < minViews)                      continue;

    seen.add(id);

    out.push({
      videoId: id,
      title: sn.title || '',
      channelTitle: sn.channelTitle || '',
      channelId: sn.channelId || '',
      description: sn.description || '',
      tags: sn.tags || [],
      publishedAt: sn.publishedAt || '',
      duration,
      viewCount,
      likeCount,
      commentCount,
      engagementRate: viewCount ? (likeCount + commentCount) / viewCount : 0,
      thumbnails: sn.thumbnails || {},
      categoryId: sn.categoryId || '',
      defaultLanguage: sn.defaultLanguage || '',
      url: `https://www.youtube.com/watch?v=${id}`,
      channelUrl: sn.channelId ? `https://www.youtube.com/channel/${sn.channelId}` : undefined,
    });
  }

  if (!out.length) {
    return [{
      json: {
        error: 'æ²¡æœ‰ç¬¦åˆæ¡ä»¶çš„è§†é¢‘',
        _why: totalIn === 0
          ? 'ä¸Šæ¸¸HTTPè¿”å›ç»“æ„ä¸åœ¨ json.items / json.body.items / json.data.items / ç›´æ¥æ•°ç»„ ä¹‹å†…'
          : 'å…¨éƒ¨è¢«è¿‡æ»¤æ¡ä»¶å‰”é™¤',
        _applied: {
          minViews, minDuration,
          afterDate: afterDate ? afterDate.toISOString() : null,
          totalIn
        },
        _peekOfInput0: $input.first()?.json ?? null
      }
    }];
  }

  // âœ… åŠ å…¥æ’åºé€»è¾‘ï¼šæŒ‰è§‚çœ‹é‡ï¼ˆviewCountï¼‰é™åºæ’åˆ—
  out.sort((a, b) => b.viewCount - a.viewCount);

  return out.map(x => ({ json: x }));

} catch (e) {
