}

function normalizeStringArray(input) {
  if (Array.isArray(input)) {
    return input
      .map(i => (typeof i === 'string' ? i.trim() : String(i)))
      .filter(i => !!i);
  }

  if (typeof input === 'string') {
    return input
      .split(',')
      .map(i => i.trim())
      .filter(i => !!i);
  }

  return [];
}

